import React from 'react';

class SmallExplain extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            return (
                <div id="explainContainer">
                    <div className="allText">
                        <p id="smallButtonTag">Multi-User</p>
                        <p id="smallExplain">Multiple converter buyers from a single physical location.</p>
                    </div>

                    <style jsx>
                        {`
                        
                        #explainContainer {
                            transform: translate(0%, -75px)
                        }

                        .allText {
                            transform: translate(0%, 0%)
                        }

                        #smallButtonTag {
                            
                        }
                        
                        #smallExplain {
                            color: white;
                            pointer-events: none;
                            transform: translate(0%, 5px)
                        }
                        
                        @media only screen and (max-width: 608px) {
                            #explainContainer{
                                transform: translate(0%, 0%)
                            }
                        }

                        #smallButtonTag {
                            color: black;
                            text-decoration: underline;
                            position: relative;
                            font-weight: 600;
                            font-size: 1.5em;
                            transform: translate(0%, 0%)
                          }

                          #soloButtonTag {
                            
                        }

                        #soloExplain {
                            color: white;
                            pointer-events: none;
                            width: 400px;
                            height: 50px;
                            margin-left: 175px;
                            top: 10px;
                            transform: translate(0%, 20px)
                        }
                        
                        `}
                    </style>
                </div>
            )
        }
    }

export default SmallExplain;
