import React from 'react';

class SmallExplain extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            return (
                <div id="explainContainer">
                    <div className="allText">
                        <p id="smallButtonTag">Multi-User</p>
                        <p id="smallExplain">Multiple converter buyers from a single physical location.</p>
                    </div>

                    <style jsx>
                        {`
                        
                        #explainContainer {
                            transform: translate(0%, -75px)
                        }

                        .allText {
                            transform: translate(0%, 0%)
                        }
                        
                        #smallExplain {
                            color: white;
                            pointer-events: none;
                            transform: translate(0%, 5px)
                        }

                        #smallButtonTag {
                            font-weight: 600;
                            font-size: 1.5em;
                            color: black;
                            text-decoration: underline;
                            transform: translate(0%, 6px)
                        }
                        
                        
                        
                        @media (max-width: 720px) {

                            #explainContainer{
                                width: 100%;
                                height: 50px;
                                position: absolute;
                            }

                            .allText {
                                position: absolute;
                                width: 100%;
                                height: 100%;
                                margin: auto;
                            }

                            #smallButtonTag {
                                position: absolute;
                                height: 50px;
                                width: 200px;
                                transform: translate(0%, 6px)
                            }

                            #soloExplain {
                                color: white;
                                pointer-events: none;
                                width: 400px;
                                height: 50px;
                                margin-left: 175px;
                                top: 10px;
                                transform: translate(0%, 20px)
                            }

                    }
                        
                        `}
                    </style>
                </div>
            )
        }
    }

export default SmallExplain;
