import React from 'react';
import MapImage from '../public/assets/map-image.png';
import CloseButton from './CloseButton';

class ContactContent extends React.Component {
    constructor(props) {
      super(props);

    }

    render() {
      if (this.props.show) {
            return (
            <div id="contentContainer">
              <p id="contactText">Contact info goes here</p>
              <div className="contentCanvas">
              <iframe id="mapIMG" src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2614.842273856716!2d-122.37878868415751!3d49.051622495035296!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x5485cbdbf3c4f225%3A0x7305006b11087fa0!2sCCON%20Metals%20Inc.!5e0!3m2!1sen!2sca!4v1584396714525!5m2!1sen!2sca" width="400" height="300" frameborder="1" style="border:0;" allowfullscreen="" aria-hidden="false" tabindex="0"></iframe>
              </div>

              <a onClick={this.props.onClose} id="closeLink"><CloseButton id="close" /></a>

                <style jsx>
                    {`

                #closeLink {
                  position: absolute;
                  width: 25px;
                  height: 25px;
                  cursor: pointer;
                  right: 1%;
                  top: 1%;
                  }

                #contentContainer {
                  height: 100%;
                  width: 100%;
                  background-image: url('data:image/png;base64,');
                  background-color: rgb(192,192,192);
                  border-radius: 8px;
                  min-width: 450px;
                  margin: auto;
                }

                @media (max-width: 720px) {
                  #contentContainer {
                    height: 73vh;
                    min-height: 575px;
                    top: 9%;
                  }
                }
                
                #mapImg {
                  width: 100%;
                  height: 80%;
                  border-radius: 12px;
                  position: relative;
                  transform: translate(0%, 12.5%);
                }

                @media (max-width: 720px) {
                  #mapImg {
                    height: 300px;
                  }
                }
              
                .contentCanvas {
                  width: 55%;
                  height: 100%;
                  transform: translate(0%, 0%);
                  right: 0px;
                  position: absolute;
                  margin-right: 5%;
                }


                #contactText {
                  width: 40%;
                  position: absolute;
                  top: 40%;
                }

                @media (max-width: 720px) {
                  #contactText {
                    width: 80%;
                    position: absolute;
                    top: 70%;
                    left: 10%
                  }

                  .contentCanvas {
                    width: 80%;
                  }
                }
                
                `}
              </style>
            </div>
     )} else {
       return null;
     }
   }
}

export default ContactContent;
