import React from 'react';
import './SoloContent.css';

class SoloContent extends React.Component {
    constructor(props) {
        super(props);

    }

    render() {
        if (this.props.visible === true && this.props.pageNum === 1) {
            return (
                    <div id="contentContainer">
                        <div className="contentCanvas">
                            <ul id="soloProList">
                                <li id="soloPro1"></li>
                                <li id="soloPro1"></li>
                                <li id="soloPro1"></li>
                            </ul>
                        </div>
                    </div>
            )} else if (this.props.visible === true && this.props.pageNum === 2) {
                return (
                    <div id="contentContainer">
                        <div className="contentCanvas">
                            <ul id="soloProList">
                                <li id="soloPro1">Small lot assays ranging from 10-10,000Lbs</li>
                                <li id="soloPro1">Detailed invoicing reflecting current PGM market values</li>
                                <li id="soloPro1">Fast order grading and processing with multiple payment options</li>
                            </ul>
                        </div>
                    </div>
            )} else if (this.props.visible === true && this.props.pageNum === 3) {
                return (
                    <div id="contentContainer">
                        <div className="contentCanvas">
                            <ul id="soloProList">
                                <li id="soloPro1">Never getting a finite price from a buying app, taking risks buying on a range</li>
                                <li id="soloPro1">Always carrying the risk of loss when buying unknown pieces</li>
                                <li id="soloPro1">Manually keeping track of everything you've purchased</li>
                            </ul>
                        </div>
                    </div>
            )} else {
                return (null)
            };
    }
}

export default SoloContent;
