import React, { Component } from 'react';
import BackgroundVideo from './video/BackgroundVideo';
import BoxFillVideo from './video/BoxFillLoop.mp4';
import SoloButton from './SoloButton.js';
import SmallButton from './SmallButton.js';
import CorpButton from './CorpButton.js';
import SoloContent from './SoloContent';
import SmallContent from './SmallContent';
import CorpContent from './CorpContent';
import CloseButton from './CloseButton';
import ChevronRight from './ChevronRight';
import ChevronLeft from './ChevronLeft';
import Contact from './Contact';
import ContactContent from './ContactContent';
import Revolution from './Revolution';


class App extends Component {
  constructor(props) {
    super(props);

    // State management and bindings
    this.state = {
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
      soloTransition: "",
      smallTransition: "",
      corpTransition: "",
    };

    this.seeSolo = this.seeSolo.bind(this);
    this.closeAll = this.closeAll.bind(this);
    this.seeSmall = this.seeSmall.bind(this);
    this.seeCorp = this.seeCorp.bind(this);
    this.pageRight = this.pageRight.bind(this);
    this.pageLeft = this.pageLeft.bind(this);
    this.seeContact = this.seeContact.bind(this);
    this.seeRevolution = this.seeRevolution.bind(this);
  }

  // Function objects

  closeAll() {
    this.setState({
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
    });
  }

  seeSolo() {
    this.setState(state => ({
      viewSolo: true,
    }));
  }

  seeSmall() {
    this.setState({
      viewSmall: true,
    });
  }

  seeCorp() {
    this.setState({
      viewCorp: true,
    });
  }

  seeContact() {
    this.setState({
      viewContact: true,
    });
  }

  seeRevolution() {
    this.setState({
      viewRevolution: true,
    });
  }

  setSoloSlideIn() {
    this.setState({
      soloTransition: "slideIn"
    })
  }

  setSoloSlideOut() {
    this.setState({
      soloTransition: "slideOut"
    })
  }

  setSmallSlideIn() {
    this.setState({
      smallTransition: "slideIn"
    })
  }

  setSmallSlideOut() {
    this.setState({
      smallTransition: "slideOut"
    })
  }

  setCorpSlideIn() {
    this.setState({
      corpTransition: "slideIn"
    })
  }

  setCorpSlideOut() {
    this.setState({
      corpTransition: "slideOut"
    })
  }

  pageRight() {
    if (this.state.contentPage <= 2) {
    this.setState({
      contentPage: this.state.contentPage + 1
    })} else if (this.state.contentPage = 3) {
      this.setState({
        contentPage: 1
      })
    }
  }

  pageLeft() {
    if (this.state.contentPage >= 2) {
    this.setState({
      contentPage: this.state.contentPage - 1
    })} else if (this.state.contentPage = 1) {
    this.setState({
        contentPage: 3
      })
    }
  }


  // Rendered objects
  render() {
    return (
      <div className="App">

        <video type="video/mp4" autoPlay loop muted
        style={{
          position: "absolute",
          width: "100vw",
          left: "50%",
          top: "50%",
          height: "100vh",
          objectFit: "cover",
          transform: "translate(-50%, -50%)",
          zIndex: "-1",
          opacity: "0.3"
        }}
        >
        <source src={BoxFillVideo} />
        </video>
        <div className="BackgroundVideo">
          <BackgroundVideo />
        </div>

        <div className="Signature">
        <img id="logo" src={require("./assets/CCON Triangle - PNG vector.png")} />
            <h1 className="sigText">
                METALS
            </h1>
        </div>

        <div id='bannerDiv'>
          <h2 id="banner">
            <a id="revLink" onClick={this.seeRevolution} style={{cursor:'pointer'}}>Your _______ <i id="revTag">REVOLUTION</i> in the catalytic converter market</a>
          </h2>
        </div>

         <div id="buttonContainer" >
         <a onClick={this.seeSolo}  id="soloButton" style={{cursor:'pointer'}}><SoloButton id="soloButton" showComponent={this.seeSolo} visible={this.state.viewSolo} buttonHide={this.state.viewSmall || this.state.viewCorp} /></a>
         <a onClick={this.seeSmall}  id="smallButton" style={{cursor:'pointer'}}><SmallButton id="smallButton" visible={this.state.viewSmall} buttonHide={this.state.viewSolo || this.state.viewCorp} /></a>
         <a onClick={this.seeCorp}  id="corpButton" style={{cursor:'pointer'}}><CorpButton id="corpButton" visible={this.state.viewCorp} buttonHide={this.state.viewSmall || this.state.viewSolo} /></a>
         <div id="footerContainer">
          <a onClick={this.seeContact} id="contactButton" style={{cursor:'pointer'}} id="footerLink"><Contact buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp || this.state.viewContact || this.state.viewRevolution}></Contact></a>
        </div>
         </div>

         <div>
            <a onClick={this.pageRight} style={{cursor:'pointer'}}><ChevronRight buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp}/></a>
            <a onClick={this.pageLeft} style={{cursor:'pointer'}}><ChevronLeft buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp}/></a>
            <a onClick={this.closeAll} style={{cursor:'pointer'}}><CloseButton buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp || this.state.viewContact || this.state.viewRevolution}/></a>
            <SoloContent visible={this.state.viewSolo} pageNum={this.state.contentPage} id="soloContent" />
            <SmallContent visible={this.state.viewSmall} pageNum={this.state.contentPage} id="smallContent" />
            <CorpContent visible={this.state.viewCorp} pageNum={this.state.contentPage} id="corpContent" />
            <ContactContent visible={this.state.viewContact} id="contactContent" />
            <Revolution visible={this.state.viewRevolution} id="revContent" />
         </div> 

         {/* CSS begins */}

         <style jsx>

            {`

            .Signature {
              font-family: Syncopate;
              color: rgb(192, 192, 192);
              position: fixed;
              left: -30px;
              top: -30px;
              z-index: 5;
              opacity: 1;
            }

            #logo {
              width: 250px;
              max-width: 250px;
              z-index: -1;
              pointer-events: none
            }

            #revTag {
              color: red;
              z-index: 2;
              height: 100%;
            }

            #revLink {
              font-size: 5.0vh;
              height: 100%;
              text-shadow: 3px 3px black;
            }

            .sigText {
              font-family: Syncopate;
              font-size: 2em;
              margin-top: -58%;
              margin-left: 61%;
              border-top: 1px solid red;
              border-bottom: 1px solid red;
              width: 7.5em;
              height: 0.95em;
            }

            #banner {
              border-top: 1px solid red;
              border-bottom: 1px solid red;
              width: 80vw;
              margin: auto;
              color: white;
              z-index: 1;
              opacity: 0.8;
            }

            #bannerWords {

            }

            div #buttonContainer {
                position: absolute;
                width: 80%;
                left: 50%;
                top: 50%;
                transform: translate(-50%, -50%);
                display: flex;
                height: 15vw;
                justify-content: space-evenly;
            }

            #corpButton{
              width: 15vw;
              min-width: 150px;
              min-height: 150px;
            }

            #smallButton{
              width: 15vw;
              min-width: 150px;
              min-height: 150px;
            }

            #soloButton{
              width: 15vw;
              min-width: 150px;
              min-height: 150px;
            }

            div #contentContainer {
              position: absolute;
              left: 50%;
              top: 50%;
              transform: translate(-50%, -50%);
              display: flex;
              align-items: center;
              justify-content: space-between;
              height: 60vh;
              width: 80vw;
              background-color: white;
              border-radius: 8px;

            }

            #footerContainer {
                width: 33%;
                position: fixed;
                transform: translate(0%, 500%);
                margin-top: 20%;
            }

            #soloButton{
              z-index: 20;
            }


            @media only screen and (max-width: 608px){

            #footerContainer {
                width: 100%;
                position: absolute;
                transform: translate(70%, 15%);
                margin-top: 0;
            }

            div #buttonContainer{
              display: inline-block;
              justify-content: space-evenly;
              align-content: center;
              width: 150px;
              height: 60%;
              top: 60%;
              transform: translate(-120%, -65%);
            }

            #banner{
              display: none;
            }

            #corpButton{

            }

            #smallButton{

            }

            #soloButton{

            }

            #banner {
              border-top: 1px solid red;
              border-bottom: 1px solid red;
              width: 60vw;
              margin: auto;
              margin-top: -2%;
              z-index: 1;
            }

            #soloButton{
              z-index: 30;
            }

            div #circle {

              background-color: hsl(0,0%,90%);
              box-shadow: inset hsla(0,0%,15%,  1) 0  0px 0px 4px, /* border */
              inset hsla(0,0%,15%, .8) 0 -1px 5px 4px, /* soft SD */
              inset hsla(0,0%,0%, .25) 0 -1px 0px 7px, /* bottom SD */
              inset hsla(0,0%,100%,.7) 0  2px 1px 7px, /* top HL */

              hsla(0,0%, 0%,.15) 0 -5px 6px 4px, /* outer SD */
              hsla(0,0%,100%,.5) 0  5px 6px 4px; /* outer HL */ 

              transition: color .2s;

              /* Brushed Metal */
              min-width: 150px;
              min-height: 150px;
              width: 100%;
              height: 100%;
              line-height: 1px;
              border-radius: 50%;
              background-image: 
              -webkit-radial-gradient(  50%   0%,  8% 50%, hsla(0,0%,100%,.5) 0%, hsla(0,0%,100%,0) 100%),
              -webkit-radial-gradient(  50% 100%, 12% 50%, hsla(0,0%,100%,.6) 0%, hsla(0,0%,100%,0) 100%),
              -webkit-radial-gradient(   0%  50%, 50%  7%, hsla(0,0%,100%,.5) 0%, hsla(0,0%,100%,0) 100%),
              -webkit-radial-gradient( 100%  50%, 50%  5%, hsla(0,0%,100%,.5) 0%, hsla(0,0%,100%,0) 100%),

              -webkit-repeating-radial-gradient( 50% 50%, 100% 100%, hsla(0,0%,  0%,0) 0%, hsla(0,0%,  0%,0)   3%, hsla(0,0%,  0%,.1) 3.5%),
              -webkit-repeating-radial-gradient( 50% 50%, 100% 100%, hsla(0,0%,100%,0) 0%, hsla(0,0%,100%,0)   6%, hsla(0,0%,100%,.1) 7.5%),
              -webkit-repeating-radial-gradient( 50% 50%, 100% 100%, hsla(0,0%,100%,0) 0%, hsla(0,0%,100%,0) 1.2%, hsla(0,0%,100%,.2) 2.2%),

              -webkit-radial-gradient( 50% 50%, 200% 50%, hsla(0,0%,90%,1) 5%, hsla(0,0%,85%,1) 30%, hsla(0,0%,60%,1) 100%);

            }

            #circle:before, #circle:after {
              content: "";
              top: 0%;
              left: 0%;
              position: relative;
              width: inherit;
              height: inherit;
              border-radius: inherit;

              /* fake conical gradients */
              background-image: 
                -webkit-radial-gradient(  50%   0%, 10% 50%, hsla(0,0%,0%,.1) 0%, hsla(0,0%,0%,0) 0%),
                -webkit-radial-gradient(  50% 100%, 10% 50%, hsla(0,0%,0%,.1) 0%, hsla(0,0%,0%,0) 0%),
                -webkit-radial-gradient(   0%  50%, 50% 10%, hsla(0,0%,0%,.1) 0%, hsla(0,0%,0%,0) 0%),
                -webkit-radial-gradient( 100%  50%, 50% 06%, hsla(0,0%,0%,.1) 0%, hsla(0,0%,0%,0) 0%);
            }
            #circle:before { transform: rotate( 65deg); }
            #circle:after { transform: rotate(-65deg); }

            #circle:active {
              color: hsl(210, 100%, 40%);
            text-shadow: hsla(210,100%,20%,.3) 0 -1px 0, hsl(210,100%,85%) 0 2px 1px, hsla(200,100%,80%,1) 0 0 5px, hsla(210,100%,50%,.6) 0 0 20px;
            box-shadow: 
              inset hsla(210,100%,30%,  1) 0  0px 0px 4px, /* border */
              inset hsla(210,100%,15%, .4) 0 -1px 5px 4px, /* soft SD */
              inset hsla(210,100%,20%,.25) 0 -1px 0px 7px, /* bottom SD */
              inset hsla(210,100%,100%,.7) 0  2px 1px 7px, /* top HL */

              hsla(210,100%,75%, .8) 0  0px 3px 2px, /* outer SD */
              hsla(210,50%,40%, .25) 0 -5px 6px 4px, /* outer SD */
              hsla(210,80%,95%,   1) 0  5px 6px 4px; /* outer HL */
            }


            /* End Brushed Metal */


            div #buttons {
              justify-content: center;
              align-content: center;
              height: 100%;
            }

            #soloButtonTag {
              color:  white;
              text-decoration: underline;
              position: relative;
              margin-top: 30%;
              left: 0vw;
              font-weight: 600;
              font-size: 1.5em;
            }

            #smallButtonTag {
              color:  white;
              text-decoration: underline;
              position: relative;
              margin-top: 50%;
              left: 0vw;
              font-weight: 600;
              font-size: 1.5em;
            }

            #corpButtonTag {
              color:  white;
              text-decoration: underline;
              position: relative;
              font-weight: 600;
              font-size: 1.5em;
              height: 1.5em;
              width: 200px;
              left: 50%;
              top: 50%;
              transform: translate(-50%, -66%);
            }

          .corpButtonImage {
            position: relative;
            height: 60%;
            width: 80%;
            min-width: 100px;
            min-height: 90px;
            transform: translate(0%, 30%);
          }

          .soloButtonImage {
            position: relative;
            height: 80%;
            width: 80%;
            min-width: 100px;
            min-height: 90px;
            transform: translate(0%, 10%);
          }

          .smallButtonImage {
            position: relative;
            height: 60%;
            width: 80%;
            min-width: 100px;
            min-height: 90px;
            transform: translate(0%, 30%);
          }


          @media only screen and (max-width: 608px) {
            #soloButtonTag {
              transform: translate(140%, -120px);
            }

            #smallButtonTag {
              transform: translate(140%, -130px);
            }

            #corpButtonTag {
              transform: translate(55%, -150px);
            }

            div #buttons {
              height: 150px;
              width: 150px;
              margin-bottom: 40px;
            }

            div #circle {
              margin: auto;
              margin-top: 3vw;
              max-width: 150px;
              max-height: 150px;
            }

          }

            @media only screen and (max-height: 860px){

              #banner{
                display: none;
              }



            }

            }

            `}

          </style>   

          {/* CSS ends */}


      </div>
    );
  }
}

export default App;
