import React from 'react';

class SoloExplain extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            if (this.props.visible === false && this.props.buttonHide === false) {
            return (
                <div >
                    <div>
                        <p id="soloButtonTag">Single-User</p>
                        <p id="soloExplain">One buyer access to the Revolution app.</p>
                    </div>

                    <style jsx>
                        {`
                        
                        #soloExplain {
                            color: white;
                            pointer-events: none;
                            transform: translate(0%, -50%)
                        }
                        
                        @media only screen and (max-width: 608px) {
                        
                        #soloExplain {
                                color: white;
                                pointer-events: none;
                                transform: translate(140%, -280%);
                            }

                        #soloButtonTag {
                            
                        }
                        }

                        #soloButtonTag {
                            color: white;
                            text-decoration: underline;
                            position: relative;
                            font-weight: 600;
                            font-size: 1.5em;
                          }
                        
                        `}
                    </style>
                </div>
            )} else {
                return null
            }
        }
    }

export default SoloExplain;
