import React from 'react';
import MapImage from '../public/assets/map-image.png';
import CloseButton from './CloseButton';


class ContactContent extends React.Component {
    constructor(props) {
      super(props);

    }

    render() {
      if (this.props.show) {
            return (
            <div id="contentContainer">
              <div id="contactInfo">
                <p id="contactText">Contact Us: revolution@ccon.ca / Phone number / </p>
              </div>

                <style jsx>
                    {`

                #closeLink {
                  position: absolute;
                  width: 25px;
                  height: 25px;
                  cursor: pointer;
                  right: 1%;
                  top: 1%;
                  }

                #contentContainer {
                  height: 10%;
                  width: 50%;
                  background-image: url('data:image/png;base64,');
                  background-color: rgb(192,192,192);
                  border-radius: 8px;
                  min-width: 450px;
                  margin: auto;
                  transform: translate(110px, -185px);
                }

                #contactInfo {
                  
                }
              
                .contentCanvas {
                  width: 55%;
                  height: 100%;
                  transform: translate(0%, 0%);
                  right: 0px;
                  position: absolute;
                  margin-right: 5%;
                }

                #contactInfo {
                  width: 100%;
                  position: absolute;
                  top: 10%;
                }

                @media (max-width: 720px) {

                  #mailerDiv {
                    height: 300px;
                  }

                  #contentContainer {
                    height: 73vh;
                    min-height: 575px;
                    top: 9%;
                  }

                  #contactInfo {
                    width: 80%;
                    position: absolute;
                    top: 70%;
                    left: 10%
                  }

                  .contentCanvas {
                    width: 80%;
                  }

                }
                
                `}
              </style>
            </div>
     )} else {
       return null;
     }
   }
}

export default ContactContent;
