import React from 'react';

class SoloContent3 extends React.Component {
    constructor(props) {
        super(props);

        this.state={
          className: "flipCard",
        }

        this.flip = this.flip.bind(this);
        this.unflip = this.unflip.bind(this);
    }

    flip() {
      this.setState({ className : "is-flipped" })
    }

    unflip() {
      this.setState({ className : "flipCard" })
    }


    render() {
            return (
                  <div id="contentContainer">
                        <div className="contentCanvas">
                            <div className={this.state.className} onMouseEnter={this.flip} onMouseLeave={this.unflip} id="card1">
                              <div class="scene scene--card">
                                <div class="card">
                                  <div class="card__face card__face--front">front</div>
                                  <div class="card__face card__face--back">back</div>
                                </div>
                              </div>
                            </div>
                            <div className={this.state.className} id="card2">
                              <div class="scene scene--card">
                                  <div class="card">
                                    <div class="card__face card__face--front">front</div>
                                    <div class="card__face card__face--back">back</div>
                                  </div>
                                </div>
                              </div>
                            <div className={this.state.className} id="card3">
                              <div class="scene scene--card">
                                  <div class="card">
                                    <div class="card__face card__face--front">front</div>
                                    <div class="card__face card__face--back">back</div>
                                  </div>
                                </div>
                            </div>
                        </div>
                        <style jsx>
                          {`
                            #contentContainer {
                              position: absolute;
                              height: 100%;
                              width: 90%;
                              background-image: url('data:image/png;base64,');
                              background-color: rgb(192,192,192);
                              border-radius: 8px;
                              transform: translateX(-200%);
                              right: 25%;
                            }

                            @media (max-width: 720px) {

                              #contentContainer {
                                height: 100%;
                                min-height: 575px;
                              }
                            }
                          
                            .contentCanvas {
                              z-index: 1;
                              height: 98%;
                              width: 88%;
                              margin: auto;
                              margin-top: 0.5%
                              perspective: 600px;
                            }

                            @media (max-width: 720px) {
                              .contentCanvas {
                                transform: translate(0, 5.5%);
                              }
                            }
                          
                            .flipCard {
                              margin: auto;
                              list-style: none;
                              font-size: 1.6em;
                              width: 33%;
                              height: 100%;
                              padding: 0;
                              display: inline-block;
                              transition: transform 1s;
                              transform-style: preserve-3d;
                              position: relative;
                              cursor: pointer;
                            }

                            @media (max-width: 720px) {
                              #card {
                                
                              }
                            }
                          
                            .card__face {
                              position: absolute;
                              height: 100%;
                              width: 100%;
                              backface-visibility: hidden;
                              margin-top: 1.25%;
                            }

                            .card__face--front {
                              background: red;
                            }

                            .card__face--back {
                              background: blue;
                              transform: rotateY( 180deg );
                            }

                            .flipCard.is-flipped {
                              transform: rotateY( 180deg );
                            }

                            #card1 {
                              
                            }

                            #card2 {

                            }

                            #card3 {

                            }


                        `}
                        </style>
                  </div>
    );
  }
}

export default SoloContent3;
