import React from 'react';
import SmallContent1 from './SmallContent1';
import SmallContent2 from './SmallContent2';
import SmallContent3 from './SmallContent3';


export default class Slider extends React.Component {
    constructor(props) {
        super(props);

        this.state={
            slide1: "slide1",
            slide2: "slide2",
            slide3: "slide3",
            slideClass: "slide1",
        }

        this.slideLeft = this.slideLeft.bind(this);
        this.slideRight = this.slideRight.bind(this);

    };

    slideRight() {
        if (this.state.slideClass === this.state.slide1)
        {
            this.setState({ slideClass : this.state.slide2 })
        } 
        else if (this.state.slideClass === this.state.slide2)
        {
            this.setState({ slideClass : this.state.slide3 })
        } 
        else if (this.state.slideClass === this.state.slide3)
        {
            this.setState({ slideClass : this.state.slide1 })
        }
    }

    slideLeft() {
        if (this.state.slideClass === this.state.slide1) 
        {
            this.setState({ slideClass : this.state.slide3 })
        } 
        else if (this.state.slideClass === this.state.slide3)
        {
            this.setState({ slideClass : this.state.slide2 })
        } 
        else if (this.state.slideClass === this.state.slide2)
        {
            this.setState({ slideClass : this.state.slide1 })
        }
    }


    render() {
        if (this.props.show === true) {
        return (
            <div className="slider">
                        <div className={this.state.slideClass} >
                           <SmallContent1 />
                           <SmallContent2 />
                           <SmallContent3 />
                        </div>
                <button id="goLeft" onClick={this.slideRight}><i class="fas fa-chevron-left"></i></button>
                <button id="goRight" onClick={this.slideLeft}><i class="fas fa-chevron-right"></i></button>

                <style jsx>
                    {`

                        button {
                            position: absolute;
                            top: 50%;
                            transform: translateY(-50%);
                            width: 75px;
                            height: 75px;
                            background: none;
                            outline: red
                        }

                        #goLeft {
                            left: 5.5%;
                        }

                        #goRight {
                            right: 5.5%;
                        }

                        .slider {
                            position: relative;
                            width: 100%;
                            height: 100%;
                            box-sizing: border-box;
                            margin: 0;
                            padding: 0;
                            display: flex;
                            align-items: center;
                            overflow: hidden;
                            margin-top: 130px;
                            min-width: 450px;
                        
                        }

                        i {
                            font-size: 2vw;
                        } 

                        .slide1 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            transition: 0.5s;
                            margin: auto;
                        }

                        .slide2 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            transition: 0.5s;
                            margin: auto;
                            transform: translateX(100%)
                        }

                        .slide3 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            transition: 0.5s;
                            margin: auto;
                            transform: translateX(200%)
                        }

                    `}
                </style>
            </div>
        );} else {
            return null;
        }
    }
}
