import React from 'react';


class SmallContent1 extends React.Component {
  constructor(props) {
    super(props);

    this.state={
      className1: "flipCard",
      className2: "flipCard",
      className3: "flipCard",
    }

    this.flip1 = this.flip1.bind(this);
    this.unflip1 = this.unflip1.bind(this);
    this.flip2 = this.flip2.bind(this);
    this.unflip2 = this.unflip2.bind(this);
    this.flip3 = this.flip3.bind(this);
    this.unflip3 = this.unflip3.bind(this);
}

flip1() {
  this.setState({ className1 : "flipCard is-flipped" })
}

unflip1() {
  this.setState({ className1 : "flipCard" })
}

flip2() {
  this.setState({ className2 : "flipCard is-flipped" })
}

unflip2() {
  this.setState({ className2 : "flipCard" })
}

flip3() {
  this.setState({ className3 : "flipCard is-flipped" })
}

unflip3() {
  this.setState({ className3 : "flipCard" })
}


render() {

        return (
              <div id="contentContainer">
                    <div className="contentCanvas contentCanvas--card">
                        <div className="flipCardContainer" id="flipContainer1" onMouseEnter={this.flip1} onMouseLeave={this.unflip1}>
                          <div className={this.state.className1}  id="card1">
                              <div className="card__face card__face--front" id="soloPro1" 
                              style={{
                                backgroundImage: "url(" + `${require("../public/assets/CCON-4-data-guy.jpg")}` + ")",
                                width: "100%",
                                height:"100%",
                                backgroundRepeat: "no-repeat",
                                backgroundSize: "cover",
                                backgroundPosition: "center",
                              }}
                              >
                              </div>
                              <div className="card__face card__face--back" id="soloPro2">
                                <div style={{
                                  backgroundImage: "url(" + `${require("../public/assets/CCON-4-data-guy.jpg")}` + ")",
                                  width: "100%",
                                  height:"100%",
                                  backgroundRepeat: "no-repeat",
                                  backgroundPosition: "center",
                                  backgroundSize: "cover",
                                  filter: "blur(8px)",
                                  }}>
                                </div>
                                <p className="cardText">Card 1 Back</p>
                              </div>
                            </div>
                          </div>
                        <div className="flipCardContainer" id="flipContainer2" onMouseEnter={this.flip2} onMouseLeave={this.unflip2}>
                        <div className={this.state.className2}  id="card2">
                              <div className="card__face card__face--front" id="soloPro1" 
                              style={{
                                backgroundImage: "url(" + `${require("../public/assets/CCON-5-longhaul.jpg")}` + ")",
                                width: "100%",
                                height:"100%",
                                backgroundRepeat: "no-repeat",
                                backgroundSize: "cover",
                                backgroundPosition: "center",
                              }}
                              >
                              </div>
                              <div className="card__face card__face--back" id="soloPro2">
                                <div style={{
                                  backgroundImage: "url(" + `${require("../public/assets/CCON-5-longhaul.jpg")}` + ")",
                                  width: "100%",
                                  height:"100%",
                                  backgroundRepeat: "no-repeat",
                                  backgroundPosition: "center",
                                  backgroundSize: "cover",
                                  filter: "blur(8px)",
                                  }}>
                                </div>
                                <p className="cardText">Card 2 Back</p>
                              </div>
                            </div>
                          </div>
                        <div className="flipCardContainer" id="flipContainer3" onMouseEnter={this.flip3} onMouseLeave={this.unflip3}>
                        <div className={this.state.className3}  id="card1">
                              <div className="card__face card__face--front" id="soloPro1" 
                              style={{
                                backgroundImage: "url(" + `${require("../public/assets/CCON-369-catalyst-jars.jpg")}` + ")",
                                width: "100%",
                                height:"100%",
                                backgroundRepeat: "no-repeat",
                                backgroundSize: "cover",
                                backgroundPosition: "center",
                              }}
                              >
                              </div>
                              <div className="card__face card__face--back" id="soloPro2">
                                <div style={{
                                  backgroundImage: "url(" + `${require("../public/assets/.jpg")}` + ")",
                                  width: "100%",
                                  height:"100%",
                                  backgroundRepeat: "no-repeat",
                                  backgroundPosition: "center",
                                  backgroundSize: "cover",
                                  filter: "blur(8px)",
                                  }}>
                                </div>
                                <p className="cardText">Card 3 Back</p>
                              </div>
                            </div>
                          </div>
                    </div>
                    <style jsx>
                      {`

                        .cardText {
                          position: absolute;
                          height: 100%;
                          width: 100%;
                          transform: translateY(-65%)
                        }
                      
                        #contentContainer {
                          position: absolute;
                          height: 100%;
                          width: 90%;
                          background-image: url('data:image/png;base64,');
                          background-color: rgb(192,192,192);
                          border-radius: 8px;
                          transform: translateX(0%);
                          right: 5%;
                          overflow: hidden;
                          border: 5px solid silver;
                          box-sizing: border-box; 
                        }

                        @media (max-width: 720px) {

                          #contentContainer {
                            height: 100%;
                            min-height: 575px;
                            overflow: hidden;
                            
                          }
                        }
                      
                        .contentCanvas {
                          z-index: 1;
                          height: 100%;
                          width: 100%;
                          margin: auto;
                          margin-top: 0%;
                        
                        }

                        @media (max-width: 720px) {
                          .contentCanvas {
                            transform: translate(0, 0%);
                            min-height: 525px;
                            height: 102%;
                            width: 100%;
                            margin-top: 0%;
                            
                          }
                        }
                      
                        .flipCard {
                          margin: auto;
                          list-style: none;
                          font-size: 1.6em;
                          width: 100%;
                          height: 100%;
                          padding: 0;
                          display: inline-block;
                          transition: transform 0.5s;
                          transform-style: preserve-3d;
                          position: relative;
                          cursor: pointer;
                        }

                        @media (max-width: 720px) {
                          .flipCard {
                            width: 100%;
                            height: 100%;
                          }
                        }
                      
                        .card__face {
                          position: absolute;
                          height: 100%;
                          width: 100%;
                          backface-visibility: hidden;
                          text-align: center;
                        }

                        .card__face--front {
                          background: white;
                        }

                        .card__face--back {
                          background: black;
                          transform: rotateY( 180deg );
                        }

                        .flipCard.is-flipped {
                          transform: rotateY( 180deg );
                        }

                        @media (max-width: 720px) {
                          .card__face--back {
                            background: black;
                            transform: rotateX( 180deg );
                          }
  
                          .flipCard.is-flipped {
                            transform: rotateX( 180deg );
                          }
                        }

                        #card1 {
                          
                        }

                        @media (max-width: 720px) {
                          #card2 {
                            margin-top: -1%
                            
                          }
                        }

                        @media (max-width: 720px) {
                          #card3 {
                            margin-top: -1%
                            
                          }
                        }

                        .flipCardContainer {
                          perspective: 40rem;
                          z-index: 1;
                          height: 100%;
                          width: 33.333%;
                          margin: auto;
                          display: inline-block;
                          
                        }

                        @media (max-width: 720px) {
                          .flipCardContainer {
                            width: 100%;
                            height: 33%;
                            margin-top: 0%;
                          }
                        }


                    `}
                    </style>
              </div>
);
}
}
export default SmallContent1;
