import React from 'react';

class SmallExplain extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            if (this.props.visible === false && this.props.buttonHide === false) {
            return (
                <div>
                    <div>
                        <p id="smallExplain">Multiple converter buyers from a single physical location.</p>
                    </div>

                    <style jsx>
                        {`
                        
                        #smallExplain {
                            margin-top: 20%;
                            color: white;
                            pointer-events: none;
                        }
                        
                        @media only screen and (max-width: 608px) {
                        
                        #smallExplain {
                                color: white;
                                pointer-events: none;
                                transform: translate(140%, -200%);
                            }
                        }
                        
                        `}
                    </style>
                </div>
            )} else {
                return null
            }
        }
    }

export default SmallExplain;
