import React from 'react';

class Why extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            return (
                <div id="whyContainer">
                    <div id="whyImg">
                    <div id="whyContent">
                        <strong><u>Why we do it:</u></strong><br />
                        This here is why we do what we do. We will most likely have one or two sentences explaining our values and integrity.
                    </div>
                    </div>

                    


                    <style jsx>
                        {`
                        
                        #whyContainer {
                            height: 100%;
                            width: 200px;
                        }

                        #whyImg {
                            height: 200px;
                            width: 200px;
                            background-color: black;
                            margin: auto;
                            border-radius: 50%;
                        }

                        #whyContent {
                            color: white;
                            margin-top: 10px
                        }

                        @media (max-width: 720px) {

                            #whyContainer {
                                height: 33%;
                                width: 100%;
                            }
    
                            #whyImg {
                                height: 100%;
                                width: 100%;
                                background-color: black;
                                margin: auto;
                                border-radius: 0%;
                                position: absolute;
                            }
    
                            #whyContent {
                                color: white;
                                transform: translate(0, 0vh)
                                
                            }

                        }

                        `}
                    </style>
                </div>
            )
        }
    }

export default Why;
