import React from 'react';
import BackgroundVideo from '../public/video/BackgroundVideo';
import cconTriangle from '../public/assets/cconTriangle.png';
import SoloButton from './SoloButton';
import SmallButton from './SmallButton';
import CorpButton from './CorpButton';
import Contact from './Contact';
import ChevronRight from './ChevronRight';
import ChevronLeft from './ChevronLeft';
import CloseButton from './CloseButton';
import SoloContent from './SoloContent';
import SmallContent from './SmallContent';
import CorpContent from './CorpContent';
import ContactContent from './ContactContent';
import Revolution from './Revolution';
import {useSpring, animated} from 'react-spring';



class App extends React.Component {
  constructor(props) {
    super(props);

    // State management and bindings
    this.state = {
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
      buttonOpacity: 1,
      contentOpacity: 0,
    };

    this.seeSolo = this.seeSolo.bind(this);
    this.closeAll = this.closeAll.bind(this);
    this.seeSmall = this.seeSmall.bind(this);
    this.seeCorp = this.seeCorp.bind(this);
    this.pageRight = this.pageRight.bind(this);
    this.pageLeft = this.pageLeft.bind(this);
    this.seeContact = this.seeContact.bind(this);
    this.seeRevolution = this.seeRevolution.bind(this);
    this.changeSolo = this.changeSolo.bind(this);
  }

  // Function objects

  closeAll() {
    this.setState({
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
    });
  }

  seeSolo() {
    this.setState({
      viewSolo: true,
    });
  }

  changeSolo() {
    this.setState({
      viewSolo: true,
    });
  }

  seeSmall() {
    this.setState({
      viewSmall: true,
    });
  }

  seeCorp() {
    this.setState({
      viewCorp: true,
    });
  }

  seeContact() {
    this.setState({
      viewContact: true,
    });
  }

  seeRevolution() {
    this.setState({
      viewRevolution: true,
    });
  }

  pageRight() {
    if (this.state.contentPage <= 2) {
    this.setState({
      contentPage: this.state.contentPage + 1
    })} else if (this.state.contentPage = 3) {
      this.setState({
        contentPage: 1
      })
    }
  }

  pageLeft() {
    if (this.state.contentPage >= 2) {
    this.setState({
      contentPage: this.state.contentPage - 1
    })} else if (this.state.contentPage = 1) {
    this.setState({
        contentPage: 3
      })
    }
  }

  showButtons() {
    if (this.state.viewSmall || this.state.viewCorp || this.state.viewRevolution || this.state.viewContact || this.state.viewSolo) {
      return true
    }
  }


  // Rendered objects
  render() {

    return (
      <div className="App">

        <BackgroundVideo type='video/mp4' autoplay />


        <div className="Signature">
        <img id="logo" src={cconTriangle} />
            <h1 className="sigText">
                METALS
            </h1>
        </div>

        <div id='bannerDiv'>
          <h2 id="banner">
            <a id="revLink" onClick={this.seeRevolution} style={{cursor:'pointer'}}>Your _______ <i id="revTag">REVOLUTION</i> in the catalytic converter market</a>
          </h2>
        </div>


         <div id="buttonContainer" className={this.showButtons()?'fadeOut':'fadeIn'}>

            <a onClick={this.seeSolo}  id="soloButton" style={{cursor:'pointer'}}>
              <SoloButton 
                showComponent={this.seeSolo} 
                visible={this.state.viewSolo} 
                buttonHide={this.state.viewSmall || this.state.viewCorp || this.state.viewRevolution || this.state.viewContact} 
                contentOpacity={this.state.contentOpacity} 
                buttonOpacity={this.state.buttonOpacity} 
              />
            </a>

            <a onClick={this.seeSmall}  id="smallButton" style={{cursor:'pointer'}}>
              <SmallButton 
                visible={this.state.viewSmall} 
                buttonHide={this.state.viewSolo || this.state.viewCorp || this.state.viewRevolution || this.state.viewContact} 
                contentOpacity={this.state.contentOpacity} 
                buttonOpacity={this.state.buttonOpacity} 
              />
            </a>

            <a onClick={this.seeCorp}  id="corpButton" style={{cursor:'pointer'}}>
              <CorpButton 
                visible={this.state.viewCorp} 
                buttonHide={this.state.viewSmall || this.state.viewSolo || this.state.viewRevolution || this.state.viewContact} 
                contentOpacity={this.state.contentOpacity} 
                buttonOpacity={this.state.buttonOpacity} 
              />
            </a>

              <div id="footerContainer">

                <a onClick={this.seeContact} id="contactButton" style={{cursor:'pointer'}} id="footerLink">
                  <Contact 
                    buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp || this.state.viewContact || this.state.viewRevolution} 
                    contentOpacity={this.state.contentOpacity} 
                    buttonOpacity={this.state.buttonOpacity} 
                  >
                  </Contact>
                </a>

              </div>
         </div>

         <div className={this.showButtons()?'fadeIn':'fadeOut'}>
