import React from 'react';

class What extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
            imgURL: './assets/what-we-do.jpg'
        }

        };

        render() {
            return (
                <div id="whatContainer">
                    <div >
                        <img id="whatImg" src={this.state.imgURL} style={{border: '3px red solid'}}></img>
                    <div id="whatContent">
                        <strong><u>What we do:</u></strong><br />
                        We are the largest volume buyer of catalytic converters in western Canada.
                    </div>
                    </div>

                    


                    <style jsx>
                        {`

                        #whatContainer {
                            height: 100%;
                            width: 200px;
                            text-align:center;
                            justify-content: center;
                        }

                        #whatImg {
                            height: 200px;
                            width: 200px;
                            background-color: black;
                            margin: auto;
                            border-radius: 50%;
                        }

                        #whatContent {
                            color: white;
                            margin-top: 0px;
                            transform: translate(0, 200px);
                        }

                        @media (max-width: 720px) {

                            #whatContainer {
                                height: 100%;
                                width: 100%;
                                margin: auto;
                            }
    
                            #whatImg {
                                height: 100%;
                                width: 100%;
                                background-color: black;
                                margin: auto;
                                border-radius: 0%;
                            }
    
                            #whatContent {
                                color: white;
                                transform: translate(0, 5vh);
                                width: 90%;
                                margin: auto;
                            }

                        }
                        
                        `}
                    </style>
                </div>
            )
        }
    }

export default What;
