import TextLoop from "react-text-loop";

class WordLoop extends React.Component {
    render() {
        return (
            <h3 id="loopElement">
                Your {" "}
                <TextLoop id="loopComponent" children={[" WORD HERE", " WORD HERE", " WORD HERE", " WORD HERE"]}>
                </TextLoop>{" "}
                <i id="revTag">REVOLUTION</i>

                <style jsx>{`
                    
                    #loopElement {
                        width: 100%
                    }

                    #revTag {
                        color: red;
                        z-index: 2;
                        height: 100%;
                    }

                    #loopElement {
                        margin-top: 0;
                        overflow: hidden;
                    }

                `}</style>
            </h3>
        );
    }
}

export default WordLoop;
