import React from 'react';
import BackgroundVideo from '../public/video/BackgroundVideo';
import cconTriangle from '../public/assets/cconTriangle.png';
import SoloButton from './SoloButton';
import SmallButton from './SmallButton';
import CorpButton from './CorpButton';
import Contact from './Contact';
import SliderSolo from './SliderSolo';
import SliderSmall from './SliderSmall';
import SliderCorp from './SliderCorp';
import ContactContent from './ContactContent';
import Revolution from './Revolution';
import WordLoop from './TextLoop';
import Who from './Who';
import What from './What';
import Why from './Why';
import ClickMe from './ClickMe';
import ClickAway from './ClickAway';


class App extends React.Component {
  constructor(props) {
    super(props);

    // State management and bindings
    this.state = {
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 1,
      viewContact: false,
      viewRevolution: false,
      buttonFade: "fadeIn",
      contentFade: "fadeOut",
      arrowFade: "fadeOut",
      renderButtons: true,
    };

    this.seeSolo = this.seeSolo.bind(this);
    this.closeAll = this.closeAll.bind(this);
    this.seeSmall = this.seeSmall.bind(this);
    this.seeCorp = this.seeCorp.bind(this);
    this.seeContact = this.seeContact.bind(this);
    this.seeRevolution = this.seeRevolution.bind(this);
    this.timerCloseContent = this.timerCloseContent.bind(this);
  }

  // Function objects

  closeAll() {
    this.setState({
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 1,
      viewContact: false,
      viewRevolution: false,
      buttonFade: "fadeIn",
      contentFade: "fadeOut",
      arrowFade: "fadeOut",
    });
  }

  timerCloseContent() {
    this.setState({
      buttonFade: "fadeIn",
      contentFade: "fadeOut",
      arrowFade: "fadeOut",
    })
    setTimeout(this.closeAll, 500);
  }

  seeSolo() {
    this.setState({
      viewSolo: true,
      buttonFade: "fadeOut",
      contentFade: "fadeIn",
      arrowFade: "fadeIn",
    });
  }

  seeSmall() {
    this.setState({
      viewSmall: true,
      buttonFade: "fadeOut",
      contentFade: "fadeIn",
      arrowFade: "fadeIn",
    });
  }

  seeCorp() {
    this.setState({
      viewCorp: true,
      buttonFade: "fadeOut",
      contentFade: "fadeIn",
      arrowFade: "fadeIn",
    });
  }

  seeContact() {
    this.setState({
      viewContact: true,
      buttonFade: "fadeOut",
      contentFade: "fadeIn",
      arrowFade: "fadeOut",
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      viewRevolution: false,
    });
  }

  seeRevolution() {
    this.setState({
      viewRevolution: true,
      buttonFade: "fadeOut",
      contentFade: "fadeIn",
      arrowFade: "fadeOut",
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      viewContact: false,
    });
  }

  handleBannerChange() {

  }


  render() {
    return (
      <div className="App">

        <head>
          <title>Ccon Metals Inc. - The best catalytic converter scrap price</title>
          <meta desc="Sell catalytic converters with transparency, security, and integrity."/>
        </head>



        <BackgroundVideo type='video/mp4' autoplay mute />
        <a onClick={this.closeAll}><div id="clickAway" >

        </div></a>


        <div className="Signature">
          <img id="logo" src={cconTriangle} />
            <h1 className="sigText">
                METALS
            </h1>

            <a onClick={this.seeContact} style={{cursor:'pointer'}} id="footerLink">
              <div id="footerContainer">
                <Contact />
            </div>
            </a>
        </div>

        <div id='bannerDiv' >
          <h2 id="banner" className={this.state.buttonFade} >
            <a id="revLink" onClick={this.seeRevolution} style={{cursor:'pointer'}}><WordLoop id="text-loop" /></a>
          </h2>
          <h2 id="banner2" className={this.state.contentFade}>
            <span id="spanLink">BANNER HERE</span>
          </h2>
        </div>


        <div id="bioContainer" className={this.state.buttonFade}>
            <div id="who"><Who /></div>
            <div id="what"><What /></div>
            <div id="why"><Why /></div>
        </div>

        <div id="clickMe" className={this.state.buttonFade}>
