import React from 'react';
import MapImage from '../public/assets/map-image.png';
import CloseButton from './CloseButton';

class ContactContent extends React.Component {
    constructor(props) {
      super(props);

    }

    render() {
      if (this.props.show) {
            return (
            <div id="contentContainer">
              <p id="contactText">Contact info goes here</p>
              <div className="contentCanvas">
                <img id="mapImg"  src={MapImage} />
              </div>

              <a onClick={this.props.onClose} id="closeLink"><CloseButton id="close" /></a>

                <style jsx>
                    {`

                #closeLink {
                  position: absolute;
                  width: 25px;
                  height: 25px;
                  cursor: pointer;
                  right: 1%;
                  top: 1%;
                  }

                #contentContainer {
                  position: relative;
                            width: 100%;
                            height: 100%;
                            box-sizing: border-box;
                            margin: 0;
                            padding: 0;
                            display: flex;
                            align-items: center;
                            overflow: hidden;
                            margin-top: 130px;
                            min-width: 450px;
                           
                }

                @media (max-width: 720px) {
                  #contentContainer {
                    height: 73vh;
                    min-height: 575px;
                    top: 9%;
                  }
                }
                
                #mapImg {
                  width: 100%;
                  height: 80%;
                  border-radius: 12px;
                  position: relative;
                  transform: translate(0%, 12.5%);
                }

                @media (max-width: 720px) {
                  #mapImg {
                    height: 300px;
                  }
                }
              
                .contentCanvas {
                  width: 55%;
                  height: 100%;
                  transform: translate(0%, 0%);
                  right: 0px;
                  position: absolute;
                  margin-right: 5%;
                }

                @media (max-width: 720px) {
                  .contentCanvas {
                    width: 90%;
                  }
                }

                #contactText {
                  width: 40%;
                  position: absolute;
                  top: 40%;
                }

                @media (max-width: 720px) {
                  #contactText {
                    width: 80%;
                    position: absolute;
                    top: 70%;
                    left: 10%
                  }
                }
                
                `}
              </style>
            </div>
     )} else {
       return null;
     }
   }
}

export default ContactContent;
