import React from 'react';
import BackgroundVideo from '../public/video/BackgroundVideo';
import cconTriangle from '../public/assets/cconTriangle.png';

class App extends React.Component {
  constructor(props) {
    super(props);

    // State management and bindings
    this.state = {
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
      soloTransition: "",
      smallTransition: "",
      corpTransition: "",
    };

    this.seeSolo = this.seeSolo.bind(this);
    this.closeAll = this.closeAll.bind(this);
    this.seeSmall = this.seeSmall.bind(this);
    this.seeCorp = this.seeCorp.bind(this);
    this.pageRight = this.pageRight.bind(this);
    this.pageLeft = this.pageLeft.bind(this);
    this.seeContact = this.seeContact.bind(this);
    this.seeRevolution = this.seeRevolution.bind(this);
  }

  // Function objects

  closeAll() {
    this.setState({
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
    });
  }

  seeSolo() {
    this.setState(state => ({
      viewSolo: true,
    }));
  }

  seeSmall() {
    this.setState({
      viewSmall: true,
    });
  }

  seeCorp() {
    this.setState({
      viewCorp: true,
    });
  }

  seeContact() {
    this.setState({
      viewContact: true,
    });
  }

  seeRevolution() {
    this.setState({
      viewRevolution: true,
    });
  }

  setSoloSlideIn() {
    this.setState({
      soloTransition: "slideIn"
    })
  }

  setSoloSlideOut() {
    this.setState({
      soloTransition: "slideOut"
    })
  }

  setSmallSlideIn() {
    this.setState({
      smallTransition: "slideIn"
    })
  }

  setSmallSlideOut() {
    this.setState({
      smallTransition: "slideOut"
    })
  }

  setCorpSlideIn() {
    this.setState({
      corpTransition: "slideIn"
    })
  }

  setCorpSlideOut() {
    this.setState({
      corpTransition: "slideOut"
    })
  }

  pageRight() {
    if (this.state.contentPage <= 2) {
    this.setState({
      contentPage: this.state.contentPage + 1
    })} else if (this.state.contentPage = 3) {
      this.setState({
        contentPage: 1
      })
    }
  }

  pageLeft() {
    if (this.state.contentPage >= 2) {
    this.setState({
      contentPage: this.state.contentPage - 1
    })} else if (this.state.contentPage = 1) {
    this.setState({
        contentPage: 3
      })
    }
  }


  // Rendered objects
  render() {

    return (
      <div className="App">

        <div className="BackgroundVideo">
          <BackgroundVideo />
        </div>

        <div className="Signature">
        <img id="logo" src={cconTriangle} />
            <h1 className="sigText">
                METALS
            </h1>

          <style jsx>{`

          .Signature {
            font-family: Syncopate;
            color: rgb(192, 192, 192);
            position: fixed;
            left: -30px;
            top: -30px;
            z-index: 5;
            opacity: 1;
          }

          #logo {
            width: 250px;
            max-width: 250px;
            z-index: -1;
            pointer-events: none
          }

          .sigText {
            font-family: Syncopate;
            font-size: 2em;
            margin-top: -58%;
            margin-left: 61%;
            border-top: 1px solid red;
            border-bottom: 1px solid red;
            width: 7.5em;
            height: 0.95em;
          }


          `}</style>
        </div>
{/* 
        <div id='bannerDiv'>
          <h2 id="banner">
            <a id="revLink" onClick={this.seeRevolution} style={{cursor:'pointer'}}>Your _______ <i id="revTag">REVOLUTION</i> in the catalytic converter market</a>
          </h2>
        </div>

         <div id="buttonContainer" >
         <a onClick={this.seeSolo}  id="soloButton" style={{cursor:'pointer'}}><SoloButton id="soloButton" showComponent={this.seeSolo} visible={this.state.viewSolo} buttonHide={this.state.viewSmall || this.state.viewCorp} /></a>
         <a onClick={this.seeSmall}  id="smallButton" style={{cursor:'pointer'}}><SmallButton id="smallButton" visible={this.state.viewSmall} buttonHide={this.state.viewSolo || this.state.viewCorp} /></a>
         <a onClick={this.seeCorp}  id="corpButton" style={{cursor:'pointer'}}><CorpButton id="corpButton" visible={this.state.viewCorp} buttonHide={this.state.viewSmall || this.state.viewSolo} /></a>
         <div id="footerContainer">
          <a onClick={this.seeContact} id="contactButton" style={{cursor:'pointer'}} id="footerLink"><Contact buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp || this.state.viewContact || this.state.viewRevolution}></Contact></a>
        </div>
         </div>

         <div>
            <a onClick={this.pageRight} style={{cursor:'pointer'}}><ChevronRight buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp}/></a>
