import React, { Component } from 'react';
import './App.css';
import BackgroundVideo from './video/BackgroundVideo';
import BoxFillVideo from './video/BoxFillLoop.mp4';
import SoloButton from './SoloButton.js';
import SmallButton from './SmallButton.js';
import CorpButton from './CorpButton.js';
import SoloContent from './SoloContent';
import SmallContent from './SmallContent';
import CorpContent from './CorpContent';
import CloseButton from './CloseButton';
import ChevronRight from './ChevronRight';
import ChevronLeft from './ChevronLeft';
import Contact from './Contact';
import ContactContent from './ContactContent';
import Revolution from './Revolution';


class App extends Component {
  constructor(props) {
    super(props);

    // State management and bindings
    this.state = {
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
      soloTransition: "",
      smallTransition: "",
      corpTransition: "",
    };

    this.seeSolo = this.seeSolo.bind(this);
    this.closeAll = this.closeAll.bind(this);
    this.seeSmall = this.seeSmall.bind(this);
    this.seeCorp = this.seeCorp.bind(this);
    this.pageRight = this.pageRight.bind(this);
    this.pageLeft = this.pageLeft.bind(this);
    this.seeContact = this.seeContact.bind(this);
    this.seeRevolution = this.seeRevolution.bind(this);
  }

  // Function objects

  closeAll() {
    this.setState({
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
    });
  }

  seeSolo() {
    this.setState(state => ({
      viewSolo: true,
    }));
  }

  seeSmall() {
    this.setState({
      viewSmall: true,
    });
  }

  seeCorp() {
    this.setState({
      viewCorp: true,
    });
  }

  seeContact() {
    this.setState({
      viewContact: true,
    });
  }

  seeRevolution() {
    this.setState({
      viewRevolution: true,
    });
  }

  setSoloSlideIn() {
    this.setState({
      soloTransition: "slideIn"
    })
  }

  setSoloSlideOut() {
    this.setState({
      soloTransition: "slideOut"
    })
  }

  setSmallSlideIn() {
    this.setState({
      smallTransition: "slideIn"
    })
  }

  setSmallSlideOut() {
    this.setState({
      smallTransition: "slideOut"
    })
  }

  setCorpSlideIn() {
    this.setState({
      corpTransition: "slideIn"
    })
  }

  setCorpSlideOut() {
    this.setState({
      corpTransition: "slideOut"
    })
  }

  pageRight() {
    if (this.state.contentPage <= 2) {
    this.setState({
      contentPage: this.state.contentPage + 1
    })} else if (this.state.contentPage = 3) {
      this.setState({
        contentPage: 1
      })
    }
  }

  pageLeft() {
    if (this.state.contentPage >= 2) {
    this.setState({
      contentPage: this.state.contentPage - 1
    })} else if (this.state.contentPage = 1) {
    this.setState({
        contentPage: 3
      })
    }
  }


  // Rendered objects
  render() {
    return (
      <div className="App">

        <video type="video/mp4" autoPlay loop muted
        style={{
          position: "absolute",
          width: "100vw",
          left: "50%",
          top: "50%",
          height: "100vh",
          objectFit: "cover",
          transform: "translate(-50%, -50%)",
          zIndex: "-1",
          opacity: "0.3"
        }}
        >
        <source src={BoxFillVideo} />
        </video>
        <div className="BackgroundVideo">
          <BackgroundVideo />
        </div>

        <div className="Signature">
        <img id="logo" src={require("./assets/CCON Triangle - PNG vector.png")} />
            <h1 className="sigText">
                METALS
            </h1>
        </div>

        <div id='bannerDiv'>
          <h2 id="banner">
            <a id="revLink" onClick={this.seeRevolution} style={{cursor:'pointer'}}>Your _______ <i id="revTag">REVOLUTION</i> in the catalytic converter market</a>
          </h2>
        </div>

         <div id="buttonContainer" >
         <a onClick={this.seeSolo}  id="soloButton" style={{cursor:'pointer'}}><SoloButton id="soloButton" showComponent={this.seeSolo} visible={this.state.viewSolo} buttonHide={this.state.viewSmall || this.state.viewCorp} /></a>
         <a onClick={this.seeSmall}  id="smallButton" style={{cursor:'pointer'}}><SmallButton id="smallButton" visible={this.state.viewSmall} buttonHide={this.state.viewSolo || this.state.viewCorp} /></a>
         <a onClick={this.seeCorp}  id="corpButton" style={{cursor:'pointer'}}><CorpButton id="corpButton" visible={this.state.viewCorp} buttonHide={this.state.viewSmall || this.state.viewSolo} /></a>
         <div id="footerContainer">
          <a onClick={this.seeContact} id="contactButton" style={{cursor:'pointer'}} id="footerLink"><Contact buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp || this.state.viewContact || this.state.viewRevolution}></Contact></a>
        </div>
         </div>

         <div>
            <a onClick={this.pageRight} style={{cursor:'pointer'}}><ChevronRight buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp}/></a>
            <a onClick={this.pageLeft} style={{cursor:'pointer'}}><ChevronLeft buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp}/></a>
            <a onClick={this.closeAll} style={{cursor:'pointer'}}><CloseButton buttonHide={this.state.viewSolo || this.state.viewSmall || this.state.viewCorp || this.state.viewContact || this.state.viewRevolution}/></a>
            <SoloContent visible={this.state.viewSolo} pageNum={this.state.contentPage} id="soloContent" />
            <SmallContent visible={this.state.viewSmall} pageNum={this.state.contentPage} id="smallContent" />
            <CorpContent visible={this.state.viewCorp} pageNum={this.state.contentPage} id="corpContent" />
            <ContactContent visible={this.state.viewContact} id="contactContent" />
            <Revolution visible={this.state.viewRevolution} id="revContent" />
         </div> 

         <style jsx>

            {`



            `}

          </style>   
      </div>
    );
  }
}

export default App;
