import React from 'react';

class Why extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            return (
                <div id="whyContainer">
                    <div id="whyImg">
                    <div id="whyContent">
                        <strong><u>Why we do it:</u></strong><br />
                        To bring clarity and integrity to an industry that is plagued with cloudiness and uncertainty.
                    </div>
                    </div>

                    


                    <style jsx>
                        {`
                        
                        #whyContainer {
                            height: 100%;
                            width: 200px;
                        }

                        #whyImg {
                            height: 200px;
                            width: 200px;
                            background-color: black;
                            margin: auto;
                            border-radius: 50%;
                            border: 2px black solid;
                        }

                        #whyContent {
                            
                            color: white;
                            margin-top: 0px;
                            transform: translate(0, 200px);
                        }

                        @media (max-width: 720px) {

                            #whyContainer {
                                height: 100%;
                                width: 100%;
                            }
    
                            #whyImg {
                                height: 100%;
                                width: 100%;
                                background-color: black;
                                margin: auto;
                                border-radius: 0%;
                            }
    
                            #whyContent {
                                color: white;
                                transform: translate(0, 5vh);
                                width: 90%;
                                margin: auto;
                            }

                        }

                        `}
                    </style>
                </div>
            )
        }
    }

export default Why;
