import React from 'react';

class SoloContent1 extends React.Component {
  constructor(props) {
    super(props);

    this.state={
      className1: "flipCard",
      className2: "flipCard",
      className3: "flipCard",
    }

    this.flip1 = this.flip1.bind(this);
    this.unflip1 = this.unflip1.bind(this);
    this.flip2 = this.flip2.bind(this);
    this.unflip2 = this.unflip2.bind(this);
    this.flip3 = this.flip3.bind(this);
    this.unflip3 = this.unflip3.bind(this);
}

flip1() {
  this.setState({ className1 : "flipCard is-flipped" })
}

unflip1() {
  setTimeout( function() {this.setState({ className1 : "flipCard" })}.bind(this), 0);
}

flip2() {
  this.setState({ className2 : "flipCard is-flipped" })
}

unflip2() {
  this.setState({ className2 : "flipCard" })
}

flip3() {
  this.setState({ className3 : "flipCard is-flipped" })
}

unflip3() {
  this.setState({ className3 : "flipCard" })
}


render() {
        return (
              <div id="contentContainer">
                    <div className="contentCanvas contentCanvas--card">
                        <div className="flipCardContainer" id="flipContainer1">
                          <div className={this.state.className1} onMouseEnter={this.flip1} onMouseLeave={this.unflip1} id="card1">
                              <div className="card__face card__face--front" id="soloPro1">Card 1 Front</div>
                              <div className="card__face card__face--back" id="soloPro2">Card 1 Back</div>
                          </div>
                        </div>
                        <div className="flipCardContainer" id="flipContainer2">
                          <div className={this.state.className2} onMouseEnter={this.flip2} onMouseLeave={this.unflip2} id="card2">
                              <div className="card__face card__face--front" id="soloPro1">Card 2 Front</div>
                              <div className="card__face card__face--back" id="soloPro2">Card 2 Back</div>
                          </div>
                        </div>
                        <div className="flipCardContainer" id="flipContainer3">
                          <div className={this.state.className3} onMouseEnter={this.flip3} onMouseLeave={this.unflip3} id="card3">
                              <div className="card__face card__face--front" id="soloPro1">Card 3 Front</div>
                              <div className="card__face card__face--back" id="soloPro2">Card 3 Back</div>
                          </div>
                        </div>
                    </div>
                    <style jsx>
                      {`
                        #contentContainer {
                          position: absolute;
                          height: 100%;
                          width: 90%;
                          background-image: url('data:image/png;base64,');
                          background-color: rgb(192,192,192);
                          border-radius: 8px;
                          transform: translateX(0%);
                          right: 5%;
                        }

                        @media (max-width: 720px) {

                          #contentContainer {
                            height: 100%;
                            min-height: 575px;
                          }
                        }
                      
                        .contentCanvas {
                          z-index: 1;
                          height: 98%;
                          width: 88%;
                          margin: auto;
                          margin-top: 0.5%
                        }

                        @media (max-width: 720px) {
                          .contentCanvas {
                            transform: translate(0, 5.5%);
                          }
                        }
                      
                        .flipCard {
                          margin: auto;
                          list-style: none;
                          font-size: 1.6em;
                          width: 33%;
                          height: 100%;
                          padding: 0;
                          display: inline-block;
                          transition: transform 1s;
                          transform-style: preserve-3d;
                          position: relative;
                          cursor: pointer;
                        }

                        @media (max-width: 720px) {
                          #card {
                            
                          }
                        }
                      
                        .card__face {
                          position: absolute;
                          height: 100%;
                          width: 100%;
                          backface-visibility: hidden;
                          margin-top: 1.25%;
                        }

                        .card__face--front {
                          background: red;
                        }

                        .card__face--back {
                          background: blue;
                          transform: rotateY( 180deg );
                        }

                        .flipCard.is-flipped {
                          transform: rotateY( 180deg );
                        }

                        #card1 {
                          
                        }

                        #card2 {

                        }

                        #card3 {

                        }

                        .flipCardContainer {
                          perspective: 40rem;
                          z-index: 1;
                          height: 100%;
                          width: 100%;
                          margin: auto;
                          margin-top: -1%
                        }


                    `}
                    </style>
              </div>
);
}
}
export default SoloContent1;
