import React from 'react';

class SoloContent3 extends React.Component {
    constructor(props) {
        super(props);

    }


    render() {
            return (
                  <div id="contentContainer">
                        <div className="contentCanvas">
                            <div className="flipCard" id="card1">
                                <div className="card__face card__face--front" id="soloPro1">Front</div>
                                <div className="card__face card__face--back" id="soloPro2">Back</div>
                            </div>
                            <div className="flipCard" id="card2">
                                <div className="card__face card__face--front" id="soloPro1">Front</div>
                                <div className="card__face card__face--back" id="soloPro2">Back</div>
                            </div>
                            <div className="flipCard" id="card3">
                                <div className="card__face card__face--front" id="soloPro1">Front</div>
                                <div className="card__face card__face--back" id="soloPro2">Back</div>
                            </div>
                        </div>
                        <style jsx>
                          {`
                            #contentContainer {
                              position: absolute;
                              height: 100%;
                              width: 90%;
                              background-image: url('data:image/png;base64,');
                              background-color: rgb(192,192,192);
                              border-radius: 8px;
                              transform: translateX(-200%);
                              right: 25%;
                            }

                            @media (max-width: 720px) {

                              #contentContainer {
                                height: 100%;
                                min-height: 575px;
                              }
                            }
                          
                            .contentCanvas {
                              z-index: 1;
                              height: 98%;
                              width: 88%;
                              margin: auto;
                              margin-top: 0.5%
                            }

                            @media (max-width: 720px) {
                              .contentCanvas {
                                transform: translate(0, 5.5%);
                              }
                            }
                          
                            .flipCard {
                              margin: auto;
                              list-style: none;
                              font-size: 1.6em;
                              width: 33%;
                              height: 100%;
                              padding: 0;
                              display: inline-block;
                              
                              perspective: 600px;
                            }

                            @media (max-width: 720px) {
                              #card {
                                
                              }
                            }
                          
                            .card__face {
                              position: absolute;
                              height: 100%;
                              width: 100%;
                              backface-visibility: hidden;
                            }

                            .card__face--front {
                              background: red;
                            }

                            #card1 {
                              transition: transform 1s;
                              transform-style: preserve-3d;
                            }

                            #card2 {
                              margin-left: 0.25%;
                              margin-right: 0.25%
                              transition: transform 1s;
                              transform-style: preserve-3d;
                            }

                            #card3 {
                              transition: transform 1s;
                              transform-style: preserve-3d;
                            }
                        `}
                        </style>
                  </div>
    );
  }
}

export default SoloContent3;
