import React from 'react';

class SmallContent extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        if (this.props.pageNum === 1) {
            return (
            <div id="contentContainer">
                <div className="contentCanvas">
                    <ul id="smallProList">
                        <li id="smallPro1">Full programmable invoice capabilities for direct client payments</li>
                        <li id="smallPro1">Master (Manager) and sub account (staff) program keys</li>
                    </ul>
                </div>
                <style jsx>{`
                            #contentContainer {
                              position: absolute;
                              margin-top: 175px;
                              height: 60vh;
                              margin-left: 10%;
                              width: 80vw;
                              background-image: url('data:image/png;base64,');
                              background-color: rgb(192,192,192);
                              border-radius: 8px;
                              min-width: 450px;
                            }

                            @media (max-width: 720px) {

                              #contentContainer {
                                height: 73vh;
                                min-height: 575px;
                              }
                            }
                          
                            .contentCanvas {
                              margin-top: 0px;
                              z-index: 15;
                              height: 100%;
                              width: 100%;
                              transform: translate(0, 7.5%);
                            }

                            @media (max-width: 720px) {
                              .contentCanvas {
                                transform: translate(0, 5.5%);
                              }
                            }
                          
                            #smallProList {
                              margin: auto;
                              list-style: none;
                              font-size: 1.6em;
                              width: 80%;
                              height: 90%;
                              left: 50%;
                              top: 50%;
                              transform: translate(-50%, -50%);
                              padding: 0;
                              position: absolute;
                              display: table;
                              vertical-align: middle;
                            }

                            @media (max-width: 720px) {
                              #smallProList {
                                width: 60%;
                                min-width: 300px;
                              }
                            }
                          
                            #smallPro1 {
                              display: table-row;
                            }
                        `}</style>
            </div>)
            } else if (this.props.visible === true && this.props.pageNum === 2) {
                return (
                    <div id="contentContainer">
                        <div className="contentCanvas">
                            <ul id="smallProList">
                                <li id="smallPro1">Small lot assays ranging from 10-10,000Lbs</li>
                                <li id="smallPro1">Detailed invoicing reflecting current PGM market values</li>
