import React from 'react';

class Who extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            return (
                <div id="whoContainer">
                    <div id="whoImg">
                    <div id="whoContent">
                        <strong><u>Who we are:</u></strong><br />
                        Since 2005 Ccon Metals has served the precious metal recycling industry with our fully equipped facility in Abbotsford B.C.
                    </div>
                    </div>

                    


                    <style jsx>
                        {`

                        #whoContainer {
                            height: 100%;
                            width: 200px;
                        }

                        #whoImg {
                            height: 200px;
                            width: 200px;
                            background-color: black;
                            margin: auto;
                            border: 2px black solid;
                            border-radius: 50%;
                        }

                        #whoContent {
                            color: white;
                            margin-top: 0px;
                            transform: translate(0, 200px);
                        }

                        @media (max-width: 720px) {

                            #whoContainer {
                                height: 33%;
                                width: 100%;
                                position: absolute;
                            }
    
                            #whoImg {
                                height: 100%;
                                width: 100%;
                                background-color: black;
                                margin: auto;
                                border-radius: 0%;
                            }
    
                            #whoContent {
                                color: white;
                                transform: translate(0, 5vh);
                                width: 90%;
                                margin: auto;
                            }

                        }
                        
                        `}
                    </style>
                </div>
            )
        }
    }

export default Who;
