import React from 'react';
import SoloContent1 from './SoloContent1';
import SoloContent2 from './SoloContent2';
import SoloContent3 from './SoloContent3';
import CloseButton from './CloseButton';


export default class SliderSolo extends React.Component {
    constructor(props) {
        super(props);

        this.state={
            slide1: "slide1",
            slide2: "slide2",
            slide3: "slide3",
            slideClass: "slide1",
        }

        this.slideLeft = this.slideLeft.bind(this);
        this.slideRight = this.slideRight.bind(this);

    };

    slideRight() {
        if (this.state.slideClass === this.state.slide1)
        {
            this.setState({ slideClass : this.state.slide2 })
        } 
        else if (this.state.slideClass === this.state.slide2)
        {
            this.setState({ slideClass : this.state.slide3 })
        } 
        else if (this.state.slideClass === this.state.slide3)
        {
            this.setState({ slideClass : this.state.slide1 })
        }
    }

    slideLeft() {
        if (this.state.slideClass === this.state.slide1) 
        {
            this.setState({ slideClass : this.state.slide3 })
        } 
        else if (this.state.slideClass === this.state.slide3)
        {
            this.setState({ slideClass : this.state.slide2 })
        } 
        else if (this.state.slideClass === this.state.slide2)
        {
            this.setState({ slideClass : this.state.slide1 })
        }
    }


    render() {
        if (this.props.show === true) {
        return (
            <div className="slider">
                        <div className={this.state.slideClass} >
                           <SoloContent1 />
                           <SoloContent2 />
                           <SoloContent3 />
                        </div>
                <button id="goLeft" onClick={this.slideRight}><i class="fas fa-chevron-left"></i></button>
                <button id="goRight" onClick={this.slideLeft}><i class="fas fa-chevron-right"></i></button>
                <a onClick={this.props.onClose} id="closeLink"><CloseButton id="close" /></a>

                <style jsx>
                    {`

                        .Chevron {
                            position: absolute;
                            display: block;
                            height: 50px;
                            width: 50px;
                            transform: rotate(-90deg);
                            right: -18px;
                            z-index: 10;
                        }

                        @media (max-width: 720px) {
                            .Chevron {
                            }

                        }

                        .Chevron::before,
                        .Chevron::after {
                            position:absolute;
                            display:block;
                            content:"";
                            border:25px solid transparent; /*adjust size*/
                        }
                        /*Change the four instances of 'top' below to rotate (top/right/bottom/left)*/
                        .Chevron::before {
                            top:0;
                            border-top-color:#b00; /*Chevron Color*/
                        }
                        .Chevron::after {
                            top:-10px; /*adjust thickness*/
                            border-top-color:#fff; /*Match background colour*/
                        }

                        #closeLink {
                            position: absolute;
                            width: 25px;
                            height: 25px;
                            cursor: pointer;
                            right: 6%;
                            top: 2%;
                        }

                        button {
                            position: absolute;
                            top: 50%;
                            transform: translateY(-50%);
                            width: 75px;
                            height: 75px;
                            background: none;
                            outline: red
                        }

                        #goLeft {
                            left: 5.5%;
                        }

                        #goRight {
                            right: 5.5%;
                        }

                        .slider {
                            position: relative;
                            width: 100%;
                            height: 100%;
                            box-sizing: border-box;
                            margin: 0;
                            padding: 0;
                            display: flex;
                            align-items: center;
                            overflow: hidden;
                            margin-top: 130px;
                            min-width: 450px;
                           
                        }

                        i {
                            font-size: 2vw;
                        } 

                        .slide1 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            transition: 0.5s;
                            margin: auto;
                        }

                        .slide2 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            transition: 0.5s;
                            margin: auto;
                            transform: translateX(100%)
                        }

                        .slide3 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            transition: 0.5s;
                            margin: auto;
                            transform: translateX(200%)
                        }

                    `}
                </style>
            </div>
        );} else {
            return null;
        }
    }
}
