import React from 'react';

class What extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
            imgURL: './assets/what-we-do.jpg'
        }

        };

        render() {
            return (
                <div id="whatContainer">
                    <div id="imgContainer" style={{textAlign: "center"}}>
                        <img id="whatImg" src={this.state.imgURL} style={{}}></img>
                    <div id="whatContent">
                        <strong><u>What we do:</u></strong><br />
                        We are the largest volume buyer of catalytic converters in western Canada.
                    </div>
                    </div>

                    


                    <style jsx>
                        {`

                        

                        #whatContainer {
                            height: 100%;
                            width: 200px;
                            text-align: center;
                            justify-content: center;
                        }

                        #whatImg {
                            height: 200px;
                            width: 200px;
                            background-color: black;
                            margin: auto;
                            border-radius: 50%;
                            border: 2px black solid;
                            box-sizing: border-box;
                            
                        }

                        #whatContent {
                            color: white;
                            margin-top: 0px;
                            transform: translate(0, 0px);
                        }

                        @media (max-width: 720px) {

                            #imgContainer {
                            
                            }

                            #whatContainer {
                                height: ;
                                width: 100vw;
                                margin: auto;
                                overflow: hidden;
                            }
    
                            #whatImg {
                                height: 400px;
                                width: 100vw;
                                background-color: black;
                                margin: auto;
                                border-radius: 0%;
                                border: 2px black solid;
                                box-sizing: border-box;
                                position: absolute;
                                top: 50%;
                                left: 50%;
                                transform: translate(-50%, -50%);
                                
                            }
    
                            #whatContent {
                                color: white;
                                transform: translate(0, 5vh);
                                width: 90%;
                                margin: auto;
                            }

                        }
                        
                        `}
                    </style>
                </div>
            )
        }
    }

export default What;
