import React from 'react';
import SoloContent1 from './SoloContent1';
import SoloContent2 from './SoloContent2';
import SoloContent3 from './SoloContent3';
import CloseButton from './CloseButton';


export default class SliderSolo extends React.Component {
    constructor(props) {
        super(props);

        this.state={
            slide1: "slide1",
            slide2: "slide2",
            slide3: "slide3",
            slideClass: "slide3",
        }

        this.slideLeft = this.slideLeft.bind(this);
        this.slideRight = this.slideRight.bind(this);

    };

    slideRight() {
        if (this.state.slideClass === this.state.slide1)
        {
            this.setState({ slideClass : this.state.slide2 })
        } 
        else if (this.state.slideClass === this.state.slide2)
        {
            this.setState({ slideClass : this.state.slide3 })
        } 
        else if (this.state.slideClass === this.state.slide3)
        {
            this.setState({ slideClass : this.state.slide1 })
        }
    }

    slideLeft() {
        if (this.state.slideClass === this.state.slide1) 
        {
            this.setState({ slideClass : this.state.slide3 })
        } 
        else if (this.state.slideClass === this.state.slide3)
        {
            this.setState({ slideClass : this.state.slide2 })
        } 
        else if (this.state.slideClass === this.state.slide2)
        {
            this.setState({ slideClass : this.state.slide1 })
        }
    }


    render() {
        if (this.props.show === true) {
        return (
            <div className="slider">
                        <div className={this.state.slideClass} >
                           <SoloContent1 />
                           <SoloContent2 />
                           <SoloContent3 />
                        </div>
                        
                <button id="goLeft" onClick={this.slideRight}><i className="ChevronLeft" id="left"></i></button>
                <button id="goRight" onClick={this.slideLeft}><i className="ChevronRight" id="right"></i></button>
                <a onClick={this.props.onClose} id="closeLink"><CloseButton id="close" /></a>

                <style jsx>
                    {`

                        .ChevronLeft {
                            position: absolute;
                            height: 75px;
                            width: 75px;
                            z-index: 10;
                            transform: rotate(90deg)
                        }

                        .ChevronRight {
                            position: absolute;
                            height: 75px;
                            width: 75px;
                            z-index: 10;
                            transform: rotate(-90deg)
                        }

                        #left {
                            right: 40px;
                            top: 0px;
                        }

                        #right {
                            right: -40px;
                            top: -5px;
                        }

                        .ChevronLeft::before,
                        .ChevronLeft::after {
                            position:absolute;
                            display:block;
                            content:"";
                            border:35px solid transparent; 
                        }
                        
                        .ChevronLeft::before {
                            
                            border-top-color:#b00; 
                        }
                        .ChevronLeft::after {
                            top:-10px; 
                            border-top-color:#fff; 
                        }

                        .ChevronRight::before,
                        .ChevronRight::after {
                            position:absolute;
                            display:block;
                            content:"";
                            border:35px solid transparent; 
                        }
                        
                        .ChevronRight::before {
                            
                            border-top-color:#b00;
                        }
                        .ChevronRight::after {
                            top:-10px;
                            border-top-color:#fff;
                        }

                        #closeLink {
                            position: absolute;
                            width: 25px;
                            height: 25px;
                            cursor: pointer;
                            right: 6%;
                            top: 2%;
                        }

                        button {
                            position: absolute;
                            top: 50%;
                            transform: translateY(-50%);
                            width: 75px;
                            height: 75px;
                            background: none;
                            border: none;
                            cursor: pointer;
                        }

                        button:focus {
                            outline: none;
                            box-shadow: none;
                        }

                        #goLeft {
                            left: 5.5%;
                        }

                        #goRight {
                            right: 5.5%;
                        }

                        .my-transition {
                            transition: transform;
                        }

                        .translate_100 {
                            transform: translateX(100%)
                        }

                        .translate_200 {
                            transform: translateX(200%)
                        }

                        .slider {
                            width: 100%;
                            height: 100%;
                            box-sizing: border-box;
                            display: flex;
                            align-items: center;
                            min-width: 450px;
                            overflow: hidden;
                        }

                        i {
                            font-size: 2vw;
                        } 

                        .slide1 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            margin: auto;
                            transition: transform
                        }

                        .slide2 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            margin: auto;
                            transform: translateX(100%);
                        }

                        .slide3 {
                            position: relative;
                            width: 100%;
                            height: 60vh;
                            margin: auto;
                            transform: translateX(200%);
                        }

                        @media (max-width: 720px) {
                            .slide3 {
                                height: 75vh;
                                margin-top: 6%
                            }

                            .slide2 {
                                height: 75vh;
                                margin-top: 6%
                            }

                            .slide1 {
                                height: 75vh;
                                margin-top: 6%
                            }

                            #closeLink {
                                top: 45px;
                                right: 40px;
                            }
                        }

                    `}
                </style>
            </div>
        );} else {
            return null;
        }
    }
}
