                            }

                            @media (max-width: 720px) {

                              #contentContainer {
                                height: 73vh;
                                min-height: 575px;
                              }
                            }
                          
                            .contentCanvas {
                              margin-top: 0px;
                              z-index: 15;
                              height: 100%;
                              width: 100%;
                              transform: translate(0, 7.5%);
                            }

                            @media (max-width: 720px) {
                              .contentCanvas {
                                transform: translate(0, 5.5%);
                              }
                            }
                          
                            #smallProList {
                              margin: auto;
                              list-style: none;
                              font-size: 1.6em;
                              width: 80%;
                              height: 90%;
                              left: 50%;
                              top: 50%;
                              transform: translate(-50%, -50%);
                              padding: 0;
                              position: absolute;
                              display: table;
                              vertical-align: middle;
                            }

                            @media (max-width: 720px) {
                              #smallProList {
                                width: 60%;
                                min-width: 300px;
                              }
                            }
                          
                            #smallPro1 {
                              display: table-row;
                            }
                        `}</style>
                    </div>)
            } else if (this.props.pageNum === 3 && this.props.show) {
                return (
                    <div id="contentContainer">
                        <div className="contentCanvas">
                        Small Who we are and how we help
                        </div>
                        <style jsx>{`
                            #contentContainer {
                              position: absolute;
                              margin-top: 175px;
                              height: 60vh;
                              margin-left: 10%;
