import TextLoop from "react-text-loop";

class WordLoop extends React.Component {
    render() {
        return (
            <h3 id="loopElement">
                Your {" "}
                <TextLoop id="loopComponent" children={[" Ccon", " transparency", " information", " converter"]}>
                </TextLoop>{" "}
                <i id="revTag">REVOLUTION</i>

                <style jsx>{`
                    #revTag {
                        color: red;
                        z-index: 2;
                        height: 100%;
                    }

                    #loopElement {
                        border-top: 1px solid red;
                        border-bottom: 1px solid red;
                    }

                    @media (max-width: 900px) {
                        #loopComponent {
                            display: none;
                        }
                    }
                `}</style>
            </h3>
        );
    }
}

export default WordLoop;
