import React from 'react';

class SoloExplain extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            if (this.props.visible === false && this.props.buttonHide === false) {
            return (
                <div >
                    <div id="soloExplain">
                        <p>One buyer access to the Revolution app.</p>
                    </div>

                    <style jsx>
                        {`
                        
                        #soloExplain {
                            color: white;
                            pointer-events: none;
                        }
                        
                        @media only screen and (max-width: 608px) {
                        
                        #soloExplain {
                                color: white;
                                pointer-events: none;
                                transform: translate(140%, -280%);
                            }
                        }
                        
                        `}
                    </style>
                </div>
            )} else {
                return null
            }
        }
    }

export default SoloExplain;
