import React from 'react';

class SmallExplain extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            return (
                <div id="explainContainer">
                    <div>
                        <p id="smallButtonTag">Multi-User</p>
                        <p id="smallExplain">Multiple converter buyers from a single physical location.</p>
                    </div>

                    <style jsx>
                        {`
                        
                        #explainContainer {
                            transform: translate(0%, -80%)
                        }

                        .allText {
                            transform: translate(0%, 0%)
                        }
                        
                        #soloExplain {
                            color: white;
                            pointer-events: none;
                            transform: translate(0%, 20%)
                        }
                        
                        @media only screen and (max-width: 608px) {
                            #explainContainer{
                                transform: translate(0%, 0%)
                            }
                        }

                        #soloButtonTag {
                            color: black;
                            text-decoration: underline;
                            position: relative;
                            font-weight: 600;
                            font-size: 1.5em;
                            transform: translate(0%, 0%)
                          }
                        
                        `}
                    </style>
                </div>
            )
        }
    }

export default SmallExplain;
