class App extends React.Component {
  constructor(props) {
    super(props);

    // State management and bindings
    this.state = {
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
      soloTransition: "",
      smallTransition: "",
      corpTransition: "",
    };

    this.seeSolo = this.seeSolo.bind(this);
    this.closeAll = this.closeAll.bind(this);
    this.seeSmall = this.seeSmall.bind(this);
    this.seeCorp = this.seeCorp.bind(this);
    this.pageRight = this.pageRight.bind(this);
    this.pageLeft = this.pageLeft.bind(this);
    this.seeContact = this.seeContact.bind(this);
    this.seeRevolution = this.seeRevolution.bind(this);
  }

  // Function objects

  closeAll() {
    this.setState({
      viewSolo: false,
      viewSmall: false,
      viewCorp: false,
      contentPage: 3,
      viewContact: false,
      viewRevolution: false,
    });
  }

  seeSolo() {
    this.setState(state => ({
      viewSolo: true,
    }));
  }

  seeSmall() {
    this.setState({
      viewSmall: true,
    });
  }

  seeCorp() {
    this.setState({
      viewCorp: true,
    });
  }

  seeContact() {
    this.setState({
      viewContact: true,
    });
  }

  seeRevolution() {
    this.setState({
      viewRevolution: true,
    });
  }

  setSoloSlideIn() {
    this.setState({
      soloTransition: "slideIn"
    })
  }

  setSoloSlideOut() {
    this.setState({
      soloTransition: "slideOut"
    })
  }

  setSmallSlideIn() {
    this.setState({
      smallTransition: "slideIn"
    })
  }

  setSmallSlideOut() {
    this.setState({
      smallTransition: "slideOut"
    })
  }

  setCorpSlideIn() {
    this.setState({
      corpTransition: "slideIn"
    })
  }

  setCorpSlideOut() {
    this.setState({
      corpTransition: "slideOut"
    })
  }

  pageRight() {
    if (this.state.contentPage <= 2) {
    this.setState({
      contentPage: this.state.contentPage + 1
    })} else if (this.state.contentPage = 3) {
      this.setState({
        contentPage: 1
      })
    }
  }

  pageLeft() {
    if (this.state.contentPage >= 2) {
    this.setState({
      contentPage: this.state.contentPage - 1
    })} else if (this.state.contentPage = 1) {
    this.setState({
        contentPage: 3
      })
    }
  }


