import React from 'react';

class Who extends React.Component {
    constructor (props) {
        super(props);

        this.state = {
            imgURL: './assets/what-we-do.jpg'
        }

        };

        render() {
            return (
                <div id="whoContainer">
                    <div id="imgContainer" style={{textAlign: "center"}}></div>
                    <div id="whoImg">
                    <div id="whoContent">
                        <strong><u>Who we are:</u></strong><br />
                        Since 2005 Ccon Metals has served the precious metal recycling industry with our fully equipped facility in Abbotsford B.C.
                    </div>
                    </div>

                    


                    <style jsx>
                        {`

#whatContainer {
    height: 100%;
    width: 200px;
    text-align: center;
    justify-content: center;
}

#whatImg {
    height: 200px;
    width: 200px;
    background-color: black;
    margin: auto;
    border-radius: 50%;
    border: 2px black solid;
    box-sizing: border-box;
    
}

#whatContent {
    color: white;
    margin-top: 0px;
    transform: translate(0, 0px);
}

                        @media (max-width: 720px) {

                            #imgContainer {
                                height: 100%;
                                width: 100%;
                                overflow: hidden;
                            }

                            #whoContainer {
                                height: 100%;
                                width: 100%;
                                margin: auto;
                                overflow: hidden;
                            }
    
                            #whoImg {
                                height: 100vh;
                                width: 100%;
                                max-width: 720px;
                                max-height: 400px;
                                background-color: black;
                                margin: auto;
                                border-radius: 0%;
                                border: 2px black solid;
                                box-sizing: border-box;
                                
                                top: 50%;
                                left: 50%;
                                transform: translate(0%, -30%);
                                
                            }
    
                            #whoContent {
                                color: black;
                                transform: translate(0, -370px);
                                width: 90%;
                                margin: auto;
                                font-size: calc(12px + 1.2vw);
                                text-shadow: -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, 1px 1px 0 #fff;
                            }

                        }
                        
                        `}
                    </style>
                </div>
            )
        }
    }

export default Who;
