import React from 'react';

class CorpExplain extends React.Component {
    constructor (props) {
        super(props);

        };

        render() {
            if (this.props.visible === false && this.props.buttonHide === false) {
            return (
                <div>
                    <div>
                        <p id="corpButtonTag" >Multi-Location</p>
                        <p id="corpExplain">Multiple converter buyers from several physical locations.</p>
                    </div>

                    <style jsx>
                        {`
                        
                        #corpExplain {
                            color: white;
                            pointer-events: none;
                        }
                        
                        @media only screen and (max-width: 608px) {
                        
                        #corpExplain {
                                color: white;
                                pointer-events: none;
                                transform: translate(140%, -195%);
                            }
                        }

                        `}
                    </style>
                </div>
            )} else {
                return null
            }
        }
    }

export default CorpExplain;
